import React, { useState, useEffect } from 'react';
import employeeService from '../services/employeeService';
import { List, ListItem, ListItemText, CircularProgress, Typography } from '@mui/material';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchEmployees();
  }, []);

  const fetchEmployees = async () => {
    try {
      setLoading(true);
      const response = await employeeService.getAllEmployees();
      setEmployees(response.data);
      setError(null);
    } catch (error) {
      setError('Failed to fetch employees. Please try again later.');
      console.error('Error fetching employees:', error);
    } finally {
      setLoading(false);
    }
  };

  if (error) {
    return <Typography color="error">{error}</Typography>;
  }

  return (
    <div>
      <Typography variant="h4" gutterBottom>Employee List</Typography>
      {loading ? (
        <CircularProgress />
      ) : (
        <List>
          {employees.map((employee) => (
            <ListItem key={employee.id}>
              <ListItemText primary={employee.name} secondary={`Email: ${employee.email}, Department: ${employee.department}`} />
            </ListItem>
          ))}
        </List>
      )}
    </div>
  );
};

export default EmployeeList;
