import React, { useState, useEffect } from 'react';
import employeeService from '../services/employeeService';
import { Link } from 'react-router-dom';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [department, setDepartment] = useState('');
  const [minSalary, setMinSalary] = useState('');
  const [maxSalary, setMaxSalary] = useState('');

  // Fetch employees on component mount
  useEffect(() => {
    fetchEmployees();
  }, []);

  // Fetch all employees
  const fetchEmployees = async () => {
    try {
      setLoading(true);
      const response = await employeeService.getAllEmployees();
      setEmployees(response.data);
      setError(null);
    } catch (error) {
      setError('Failed to fetch employees. Please try again later.');
      console.error('Error fetching employees:', error);
    } finally {
      setLoading(false);
    }
  };

  // Handle search
  const handleSearch = async (e) => {
    e.preventDefault();
    
    try {
      setLoading(true);
      const params = {};
      
      if (searchTerm) params.name = searchTerm;
      if (department) params.department = department;
      if (minSalary) params.minSalary = minSalary;
      if (maxSalary) params.maxSalary = maxSalary;
      
      // If no search params, fetch all employees
      if (Object.keys(params).length === 0) {
        fetchEmployees();
        return;
      }
      
      const response = await employeeService.searchEmployees(params);
      setEmployees(response.data);
      setError(null);
    } catch (error) {
      setError('Failed to search employees. Please try again later.');
      console.error('Error searching employees:', error);
    } finally {
      setLoading(false);
    }
  };

  // Handle delete
  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this employee?')) {
      try {
        await employeeService.deleteEmployee(id);
        // Refresh employee list
        fetchEmployees();
      } catch (error) {
        setError('Failed to delete employee. Please try again later.');
        console.error('Error deleting employee:', error);
      }
    }
  };

  // Reset search form
  const resetSearch = () => {
    setSearchTerm('');
    setDepartment('');
    setMinSalary('');
    setMaxSalary('');
    fetchEmployees();
  };

  return (
    <div className="container mt-4">
      <h2>Employee List</h2>
      
      {/* Search Form */}
      <div className="card mb-4">
        <div className="card-body">
          <h5 className="card-title">Search Employees</h5>
          <form onSubmit={handleSearch}>
            <div className="row g-3">
              <div className="col-md-3">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Search by name"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>
              <div className="col-md-3">
                <select
                  className="form-select"
                  value={department}
                  onChange={(e) => setDepartment(e.target.value)}
                >
                  <option value="">Select Department</option>
                  <option value="Engineering">Engineering</option>
                  <option value="HR">HR</option>
                  <option value="Marketing">Marketing</option>
                  <option value="Finance">Finance</option>
                </select>
              </div>
              <div className="col-md-2">
                <input
                  type="number"
                  className="form-control"
                  placeholder="Min Salary"
                  value={minSalary}
                  onChange={(e) => setMinSalary(e.target.value)}
                />
              </div>
              <div className="col-md-2">
                <input
                  type="number"
                  className="form-control"
                  placeholder="Max Salary"
                  value={maxSalary}
                  onChange={(e) => setMaxSalary(e.target.value)}
                />
              </div>
              <div className="col-md-2">
                <button type="submit" className="btn btn-primary me-2">Search</button>
                <button type="button" className="btn btn-secondary" onClick={resetSearch}>Reset</button>
              </div>
            </div>
          </form>
        </div>
      </div>
      
      {/* Add Employee Button */}
      <div className="mb-3">
        <Link to="/add" className="btn btn-success">
          Add New Employee
        </Link>
      </div>
      
      {/* Error Message */}
      {error && (
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
      )}
      
      {/* Loading Indicator */}
      {loading ? (
        <div className="d-flex justify-content-center">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      ) : (
        /* Employee Table */
        <div className="table-responsive">
          <table className="table table-striped table-hover">
            <thead className="table-dark">
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Email</th>
                <th>Department</th>
                <th>Salary</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {employees.length > 0 ? (
                employees.map((employee) => (
                  <tr key={employee.id}>
                    <td>{employee.id}</td>
                    <td>{employee.name}</td>
                    <td>{employee.email}</td>
                    <td>{employee.department}</td>
                    <td>${employee.salary.toLocaleString()}</td>
                    <td>
                      <Link to={`/edit/${employee.id}`} className="btn btn-sm btn-primary me-2">
                        Edit
                      </Link>
                      <button
                        className="btn btn-sm btn-danger"
                        onClick={() => handleDelete(employee.id)}
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="6" className="text-center">
                    No employees found.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default EmployeeList;
