import React, { useState, useEffect } from 'react';
import employeeService from '../services/employeeService';
import { Link } from 'react-router-dom';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Select from '@mui/material/Select';
import MenuItem from '@mui/material/MenuItem';
import InputLabel from '@mui/material/InputLabel';
import FormControl from '@mui/material/FormControl';
import CircularProgress from '@mui/material/CircularProgress';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [department, setDepartment] = useState('');
  const [minSalary, setMinSalary] = useState('');
  const [maxSalary, setMaxSalary] = useState('');

  // Fetch employees on component mount
  useEffect(() => {
    fetchEmployees();
  }, []);

  // Fetch all employees
  const fetchEmployees = async () => {
    try {
      setLoading(true);
      const response = await employeeService.getAllEmployees();
      setEmployees(response.data);
      setError(null);
    } catch (error) {
      setError('Failed to fetch employees. Please try again later.');
      console.error('Error fetching employees:', error);
    } finally {
      setLoading(false);
    }
  };

  // Handle search
  const handleSearch = async (e) => {
    e.preventDefault();
    
    try {
      setLoading(true);
      const params = {};
      
      if (searchTerm) params.name = searchTerm;
      if (department) params.department = department;
      if (minSalary) params.minSalary = minSalary;
      if (maxSalary) params.maxSalary = maxSalary;
      
      // If no search params, fetch all employees
      if (Object.keys(params).length === 0) {
        fetchEmployees();
        return;
      }
      
      const response = await employeeService.searchEmployees(params);
      setEmployees(response.data);
      setError(null);
    } catch (error) {
      setError('Failed to search employees. Please try again later.');
      console.error('Error searching employees:', error);
    } finally {
      setLoading(false);
    }
  };

  // Handle delete
  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this employee?')) {
      try {
        await employeeService.deleteEmployee(id);
        // Refresh employee list
        fetchEmployees();
      } catch (error) {
        setError('Failed to delete employee. Please try again later.');
        console.error('Error deleting employee:', error);
      }
    }
  };

  // Reset search form
  const resetSearch = () => {
    setSearchTerm('');
    setDepartment('');
    setMinSalary('');
    setMaxSalary('');
    fetchEmployees();
  };

  return (
    <div className="container mt-4">
      <h2>Employee List</h2>
      
      <form onSubmit={handleSearch} style={{ marginBottom: '20px' }}>
        <TextField
          label="Search by name"
          variant="outlined"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ marginRight: '10px' }}
        />
        <FormControl variant="outlined" style={{ minWidth: 120, marginRight: '10px' }}>
          <InputLabel>Department</InputLabel>
          <Select
            value={department}
            onChange={(e) => setDepartment(e.target.value)}
            label="Department"
          >
            <MenuItem value="">
              <em>None</em>
            </MenuItem>
            <MenuItem value="Engineering">Engineering</MenuItem>
            <MenuItem value="HR">HR</MenuItem>
            <MenuItem value="Marketing">Marketing</MenuItem>
            <MenuItem value="Finance">Finance</MenuItem>
          </Select>
        </FormControl>
        <TextField
          label="Min Salary"
          variant="outlined"
          type="number"
          value={minSalary}
          onChange={(e) => setMinSalary(e.target.value)}
          style={{ marginRight: '10px' }}
        />
        <TextField
          label="Max Salary"
          variant="outlined"
          type="number"
          value={maxSalary}
          onChange={(e) => setMaxSalary(e.target.value)}
          style={{ marginRight: '10px' }}
        />
        <Button type="submit" variant="contained" color="primary" style={{ marginRight: '10px' }}>
          Search
        </Button>
        <Button variant="outlined" color="secondary" onClick={resetSearch}>
          Reset
        </Button>
      </form>
      
      {/* Add Employee Button */}
      <div className="mb-3">
        <Link to="/add" className="btn btn-success">
          Add New Employee
        </Link>
      </div>
      
      {/* Error Message */}
      {error && (
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
      )}
      
      {/* Loading Indicator */}
      {loading ? (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
          <CircularProgress />
        </div>
      ) : (
        /* Employee Table */
        <TableContainer component={Paper}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>ID</TableCell>
                <TableCell>Name</TableCell>
                <TableCell>Email</TableCell>
                <TableCell>Department</TableCell>
                <TableCell>Salary</TableCell>
                <TableCell>Actions</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {employees.length > 0 ? (
                employees.map((employee) => (
                  <TableRow key={employee.id}>
                    <TableCell>{employee.id}</TableCell>
                    <TableCell>{employee.name}</TableCell>
                    <TableCell>{employee.email}</TableCell>
                    <TableCell>{employee.department}</TableCell>
                    <TableCell>${employee.salary.toLocaleString()}</TableCell>
                    <TableCell>
                      <Button
                        variant="contained"
                        color="primary"
                        component={Link}
                        to={`/edit/${employee.id}`}
                        style={{ marginRight: '10px' }}
                      >
                        Edit
                      </Button>
                      <Button
                        variant="contained"
                        color="secondary"
                        onClick={() => handleDelete(employee.id)}
                      >
                        Delete
                      </Button>
                    </TableCell>
                  </TableRow>
                ))
              ) : (
                <TableRow>
                  <TableCell colSpan={6} align="center">
                    No employees found.
                  </TableCell>
                </TableRow>
              )}
            </TableBody>
          </Table>
        </TableContainer>
      )}
    </div>
  );
};

export default EmployeeList;
