import React from 'react';
import { Box, Typography } from '@mui/material';

const About = () => (
  // <Box sx={{ p: 3 }}>
  //   <Typography variant="h4" gutterBottom>About Employee Management System</Typography>
  //   <Typography variant="body1">
  //     The Employee Management System is designed to streamline the process of managing employee records. 
  //     It provides features such as adding, updating, and deleting employee information, as well as viewing 
  //     detailed employee profiles. Our system ensures efficient management of employee data, helping organizations 
  //     maintain accurate records and improve operational efficiency.
  //   </Typography>
  // </Box>

  <Box sx={{ p: 3 }}>
    <Typography variant="h4" gutterBottom>About Employee Management System</Typography>
    <Typography variant="body1">
      The Employee Management System is designed to streamline the process of managing employee records. 
      It provides features such as adding, updating, and deleting employee information, as well as viewing 
      detailed employee profiles. Our system ensures efficient management of employee data, helping organizations 
      maintain accurate records and improve operational efficiency.
    </Typography>
    <Typography variant="body1" mt={2}>
      Our mission is to provide a user-friendly and reliable platform that simplifies HR tasks and empowers businesses to focus on their core objectives.
    </Typography>
  </Box>

);

export default About;
