import React, { useState, useEffect } from 'react';
import employeeService from '../services/employeeService';
import { Card, CardContent, Typography, CircularProgress, TextField, Grid, Box } from '@mui/material';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetchEmployees();
  }, []);

  const fetchEmployees = async () => {
    try {
      setLoading(true);
      const response = await employeeService.getAllEmployees();
      setEmployees(response.data);
      setError(null);
    } catch (error) {
      setError('Failed to fetch employees. Please try again later.');
      console.error('Error fetching employees:', error);
    } finally {
      setLoading(false);
    }
  };

  const filteredEmployees = employees.filter((employee) => {
    const searchStr = searchTerm.toLowerCase();
    return (
      employee.name.toLowerCase().includes(searchStr) ||
      employee.salary.toString().includes(searchStr) ||
      employee.department.toLowerCase().includes(searchStr)
    );
  });

  if (error) {
    return <Typography color="error">{error}</Typography>;
  }

  return (
    <div>
      <Typography variant="h4" gutterBottom>Employee List</Typography>
      <Box sx={{ display: 'flex', justifyContent: 'center', mb: 2 }}>
        <TextField
          label="Search by Name, Amount, or Department"
          variant="outlined"
          size="small"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          sx={{ width: '50%' }}
        />
      </Box>
      {loading ? (
        <CircularProgress />
      ) : (
        <Grid container spacing={3}>
          {filteredEmployees.map((employee) => (
            <Grid item key={employee.id} xs={12} sm={6} md={4}>
              <Card>
                <CardContent>
                  <Typography variant="h6">{employee.name}</Typography>
                  <Typography variant="body2">Email: {employee.email}</Typography>
                  <Typography variant="body2">Department: {employee.department}</Typography>
                  <Typography variant="body2">Salary: {employee.salary}</Typography>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      )}
    </div>
  );
};

export default EmployeeList;
