import React, { useState, useEffect } from 'react';
import employeeService from '../services/employeeService';
import EmployeeCard from './EmployeeCard';
import { Grid, CircularProgress, Typography, Box, TextField } from '@mui/material';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchSalary, setSearchSalary] = useState('');

  useEffect(() => {
    fetchEmployees();
  }, []);

  const fetchEmployees = async () => {
    try {
      setLoading(true);
      const response = await employeeService.getAllEmployees();
      setEmployees(response.data);
      setError(null);
    } catch (error) {
      setError('Failed to fetch employees. Please try again later.');
      console.error('Error fetching employees:', error);
    } finally {
      setLoading(false);
    }
  };

  // const calculateSalaryStats = (employees) => {
  //   const totalSalary = employees.reduce((sum, emp) => sum + emp.salary, 0);
  //   const averageSalary = (totalSalary / employees.length).toFixed(2);
  //   const highestSalary = Math.max(...employees.map(emp => emp.salary));
  //   return { totalSalary, averageSalary, highestSalary };
  // };

  const { totalSalary, averageSalary, highestSalary } = calculateSalaryStats(employees);

  const filteredEmployees = employees.filter(emp => emp.salary.toString().includes(searchSalary));

  return (
    <div className="container mt-4">
      <Typography variant="h4" gutterBottom>
        Employee List
      </Typography>

      {error && (
        <Typography variant="body1" color="error" gutterBottom>
          {error}
        </Typography>
      )}

      {loading ? (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
          <CircularProgress />
        </div>
      ) : (
        <>
          <Box sx={{ mb: 4 }}>
            <Typography variant="h6">Salary Summary</Typography>
            <Typography variant="body1">Total Salary: ${totalSalary.toLocaleString()}</Typography>
            <Typography variant="body1">Average Salary: ${averageSalary}</Typography>
            <Typography variant="body1">Highest Salary: ${highestSalary.toLocaleString()}</Typography>
          </Box>
          <TextField
            label="Search by Salary"
            variant="outlined"
            fullWidth
            margin="normal"
            value={searchSalary}
            onChange={(e) => setSearchSalary(e.target.value)}
          />
          <Grid container spacing={3} justifyContent="center">
            {filteredEmployees.length > 0 ? (
              filteredEmployees.map((employee) => (
                <Grid item key={employee.id} xs={12} sm={6} md={4} lg={3}>
                  <EmployeeCard employee={employee} />
                </Grid>
              ))
            ) : (
              <Typography variant="body1" align="center">
                No employees found.
              </Typography>
            )}
          </Grid>
        </>
      )}
    </div>
  );
};

export default EmployeeList;
